import React, { useEffect, useRef, useState } from 'react';
import './Home.css';

function Home() {
  const aboutRef = useRef(null);
  const holdingsRef = useRef(null);
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    function handleScroll() {
      if (!aboutRef.current || !holdingsRef.current) return;
      const aboutRect = aboutRef.current.getBoundingClientRect();
      const holdingsRect = holdingsRef.current.getBoundingClientRect();

      if (aboutRect.top < window.innerHeight * 0.8) {
        setVisible(true);
      }
    }

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // initial check

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="home-container">

      {/* Hero Section */}
      <section id="hero" className="section hero-section">
        <div className="heading-with-image">
          <h1 className="main-heading">LoanChain</h1>
          <img src="/images/img.webp" alt="LoanChain Logo" className="heading-image" />
          <h4 className="sub-heading">Reimagining Loans with NFTs</h4>
          <button
            className="know-more-button"
            onClick={() => {
              document.getElementById('about').scrollIntoView({ behavior: 'smooth' });
            }}
          >
            Know More
          </button>
        </div>
      </section>

      {/* Container for About and Holdings sections side by side */}
      <div className={`about-holdings-container ${visible ? 'visible' : ''}`}>

        {/* About Section */}
        <section
          id="about"
          className="section about-section"
          ref={aboutRef}
        >
          <div className="about-container">
            <h2>All Your NFT Holdings</h2>
            <h3>Check Your Eligibility</h3>
            <input
              type="text"
              placeholder="Enter your Solana Wallet Id"
              className="demo-input"
            />
            <button className="check-btn">Submit</button>
          </div>
        </section>

        {/* Holdings Section */}
        <section
          id="holdings"
          className="section holdings-section"
          ref={holdingsRef}
        >
          <div className="holdings-container">
            <h2>A Solana based NFT-Driven Lending Solution</h2>
            <h3>Check Your Eligibility</h3>
            <input
              type="text"
              placeholder="Enter your Solana Wallet Id"
              className="demo-input"
            />
            <button className="check-btn">Submit</button>
          </div>
        </section>

      </div>

      {/* Features Section */}
      <section id="features" className="section features-section">
        <h2>Features</h2>
        <ul>
          <li>Decentralized loan agreements</li>
          <li>Instant loan verification</li>
          <li>Secure NFT-backed collateral</li>
          <li>Transparent repayment schedules</li>
        </ul>
      </section>

    </div>
  );
}

export default Home;
