import React, { useEffect, useRef, useState } from 'react';
import './Home.css';

function Home() {
  const aboutRef = useRef(null);
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    function handleScroll() {
      if (!aboutRef.current) return;
      const rect = aboutRef.current.getBoundingClientRect();
      if (rect.top < window.innerHeight * 0.8) {
        setVisible(true);
      }
    }

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // initial check

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="home-container">

      {/* Hero Section */}
      <section id="hero" className="section hero-section">
        <div className="heading-with-image">
          <h1 className="main-heading">LoanChain</h1>
          <img src="/images/img.webp" alt="LoanChain Logo" className="heading-image" />
          <h4 className="sub-heading">Reimagining Loans with NFTs</h4>
          <button
            className="know-more-button"
            onClick={() => {
              document.getElementById('about').scrollIntoView({ behavior: 'smooth' });
            }}
          >
            Know More
          </button>
        </div>
      </section>

      {/* About / Demo Section */}
      {/* Second About Section with background on left */}
<section id="about-2" className="section about-section-2">
  <div className="about-2-left">
    {/* This div has background image */}
    <div className="background-container">
      <h2>Check Eligibility</h2>
      <p>Enter your wallet to see if you qualify instantly!</p>
      <input
        type="text"
        placeholder="Enter your Solana Wallet Id"
        className="demo-input"
      />
      <button className="check-btn">Check Now</button>
    </div>
  </div>
  <div className="about-2-right">
    <h2>Why Choose LoanChain?</h2>
    <p>
      Our solution leverages NFTs on Solana to provide quick, secure, and decentralized loans.
    </p>
    {/* Add more content here if you want */}
  </div>
</section>


      {/* Features Section */}
      <section id="features" className="section features-section">
        <h2>Features</h2>
        <ul>
          <li>Decentralized loan agreements</li>
          <li>Instant loan verification</li>
          <li>Secure NFT-backed collateral</li>
          <li>Transparent repayment schedules</li>
        </ul>
      </section>

    </div>
  );
}

export default Home;
