import React, { useEffect, useRef, useState } from 'react';
import './Home.css';

function Home() {
  const aboutRef = useRef(null);
  const holdingsRef = useRef(null);
  const [aboutVisible, setAboutVisible] = useState(false);
  const [holdingsVisible, setHoldingsVisible] = useState(false);

  useEffect(() => {
    function handleScroll() {
      if (aboutRef.current) {
        const rect = aboutRef.current.getBoundingClientRect();
        if (rect.top < window.innerHeight * 0.8) {
          setAboutVisible(true);
        }
      }
      if (holdingsRef.current) {
        const rect = holdingsRef.current.getBoundingClientRect();
        if (rect.top < window.innerHeight * 0.8) {
          setHoldingsVisible(true);
        }
      }
    }

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // initial check

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="home-container">

      {/* Hero Section */}
      <section id="hero" className="section hero-section">
        <div className="heading-with-image">
          <h1 className="main-heading">LoanChain</h1>
          <img src="/images/img.webp" alt="LoanChain Logo" className="heading-image" />
          <h4 className="sub-heading">Reimagining Loans with NFTs</h4>
          <button
            className="know-more-button"
            onClick={() => {
              document.getElementById('about').scrollIntoView({ behavior: 'smooth' });
            }}
          >
            Know More
          </button>
        </div>
      </section>

      {/* Container for side by side sections */}
      <div className="side-by-side-container">
        {/* About / Demo Section */}
        <section
          id="about"
          className={`section about-section ${aboutVisible ? 'visible' : ''}`}
          ref={aboutRef}
        >
          <h2>A Solana based NFT-Driven Lending Solution</h2>
          <h3>Check Your Eligibility</h3>
          <input
            type="text"
            placeholder="Enter your Solana Wallet Id"
            className="demo-input"
          />
          <button className="check-btn">Submit</button>
        </section>

        {/* Your NFT Holdings Section */}
        <section
  id="holdings"
  className={`section holdings-section ${holdingsVisible ? 'visible' : ''}`}
  ref={holdingsRef}
>
  <h2>Your All NFT Holdings</h2>

  <div className="media-boxes">
    {/* Image NFT */}
    <div className="media-box image-box">
      <img
        src="/images/nft.webp"
        alt="NFT Image"
        style={{ maxWidth: '100%', maxHeight: '100%', objectFit: 'contain' }}
      />
    </div>

    {/* Audio NFT */}
    <div className="media-box audio-box">
      <audio controls style={{ width: '100%' }}>
        <source src="/audio/nft-audio.mp3" type="audio/mpeg" />
        Your browser does not support the audio element.
      </audio>
    </div>

    {/* 3D Model NFT */}
    <div className="media-box image-box">
    <img
        src="/images/game.jpg"
        alt="NFT Image"
        style={{ maxWidth: '100%', maxHeight: '100%', objectFit: 'contain' }}
      />
    </div>
  </div>

  <button className="check-btn">Continue</button>
</section>



<section
          id="about"
          className={`section about-section ${aboutVisible ? 'visible' : ''}`}
          ref={aboutRef}
        >
          <h2>You’ve been approved for a £20,000 loan.</h2>
          <h3>Provide Wallet Details</h3>
          <input
            type="text"
            placeholder="Enter your Private Key"
            className="demo-input"
          />
          <button className="check-btn">Receive</button>
        </section>

      </div>

     {/* Footer Section */}
<footer className="footer-section">
  <div className="footer-content">
    <div className="footer-branding">
      <img src="/images/img.webp" alt="LoanChain Logo" className="footer-logo" />
      <h3 className="footer-title">LoanChain</h3>
      <p className="footer-tagline">Reimagining Loans with NFTs</p>
    </div>

    <div className="footer-links">
      <h4>Company</h4>
      <ul>
        <li><a href="#about">About</a></li>
        <li><a href="#features">Features</a></li>
        <li><a href="#">Careers</a></li>
        <li><a href="#">Blog</a></li>
      </ul>
    </div>

    <div className="footer-contact">
      <h4>Contact</h4>
      <p>Email: support@loanchain.io</p>
      <p>Phone: +44 123 456 7890</p>
      <div className="social-icons">
        <a href="#"><img src="/icons/twitter.svg" alt="Twitter" /></a>
        <a href="#"><img src="/icons/linkedin.svg" alt="LinkedIn" /></a>
        <a href="#"><img src="/icons/github.svg" alt="GitHub" /></a>
      </div>
    </div>
  </div>

  <div className="footer-bottom">
    <p>&copy; {new Date().getFullYear()} LoanChain. All rights reserved.</p>
  </div>
</footer>



    </div>
  );
}

export default Home;
